
public class Wanjeonsu {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int count =0;
		for(int i=1; i<=1000; i++) {
			//약수의 합을 저장할 변수
			int sum=0;
			//약수를 구하기위해서 1부터 자신의 절반까지
			for(int j=1; j<=i/2; j++) {
				//약수는 나누어서 나머지가 없는 수
				if(i % j == 0) {
					//약수를 sum에 추가
					sum = sum + j;
				}
			}
			
			//자신의 제외한 약수의 합이 자신과 같다면
			if(i == sum) {
				System.out.printf("완전수:%d\n", i);
				count = count + 1;
			}
		}
		System.out.printf("완전수 개수 :%d개\n", count);
	}

}
